screen.userinput:
cat > /etc/nftables/nftables.conf << "EOF" #!/sbin/nft -f
# You're using the example configuration for a setup of a firewall # from Beyond Linux From Scratch. # # This example is far from being complete, it is only meant # to be a reference. # # Firewall security is a complex issue, that exceeds the scope # of the configuration rules below. # # You can find additional information # about firewalls in Chapter 4 of the BLFS book. # http://www.&lfs-domainname;/blfs
# Drop all existing rules flush ruleset
# Filter for both ip4 and ip6 (inet) table inet filter {
# filter incomming packets chain input {
# Drop everything that doesn't match policy type filter hook input priority 0; policy drop;
# accept packets for established connections ct state { established, related } accept
# Drop packets that have a connection state of invalid ct state invalid drop
# Allow connections to the loopback adapter iifname "lo" accept
# Allow connections to the LAN1 interface iifname "LAN1" accept
# Accept icmp requests ip protocol icmp accept
# Allow ssh connections on LAN1 iifname "LAN1" tcp dport ssh accept
# Drop everything else drop }
# Allow forwarding for external connections to WAN1 chain forward {
# Drop if it doesn't match policy type filter hook forward priority 0; policy drop;
# Accept connections on WAN1 oifname "WAN1" accept
# Allow forwarding to another host via this interface # Uncomment the following line to allow connections # ip daddr 192.168.0.2 ct status dnat accept
# Allow established and related connections iifname "WAN1" ct state { established, related } accept }
# Filter output traffic chain output {
# Allow everything outbound type filter hook output priority 0; policy accept; } }
# Allow NAT for ip protocol (both ip4 and ip6) table ip nat {
chain prerouting {
# Accept on inbound interace for policy match type nat hook prerouting priority 0; policy accept;
# Accept http and https on 192.168.0.2 # Uncomment the following line to allow http and https #iifname "WAN1" tcp dport { http, https } dnat to 192.168.0.2 }
chain postrouting {
# accept outbound type nat hook postrouting priority 0; policy accept;
# Masquerade on WAN1 outbound oifname "WAN1" masquerade } } EOF
