screen.userinput:
make install install.man &&
ln -v -sf tcsh /bin/csh && ln -v -sf tcsh.1 /usr/share/man/man1/csh.1 &&
install -v -m755 -d /usr/share/doc/tcsh-&tcsh-version;/html && install -v -m644 tcsh.html/* /usr/share/doc/tcsh-&tcsh-version;/html && install -v -m644 FAQ /usr/share/doc/tcsh-&tcsh-version;
